mapper.select:
SELECT tenant.id AS tenant_id, tenant.code AS tenant_code, tenant.name AS tenant_name, tenant.business, tenant.tenant_type, tenant.create_time, agent.id AS agent_id, agent.name AS agent_name FROM tenant LEFT OUTER JOIN agent ON agent.id = tenant.agent_id AND agent.deleted = 0 WHERE tenant.deleted = 0
AND (tenant.code LIKE #{keyword} OR tenant.name LIKE #{keyword} OR agent.code LIKE #{keyword} OR agent.name LIKE #{keyword})
LIMIT #{offset}, #{rows}
